import requests
import yaml

with open("/home/neon/xdg/config/neon/neon.yaml", mode="r", encoding="utf-8") as f:
    # load config file to config variable
    config = yaml.safe_load(f)

access_token_secret = config.get("hana", {}).get("access_token_secret")

# Use the access_token_secret value as Bearer auth in request headers
headers = {"Authorization": f"Bearer {access_token_secret}"}
get_tts_data = {"lang_code": "en-us", "to_speak": "hello"}
response = requests.post("http://localhost:8082/neon/get_tts", data=get_tts_data, headers=headers, timeout=10)
assert response.status_code == 200, f"Failed to get TTS data: {response.text}"
assert response.json().get("encoded_audio"), f"Failed to get TTS data: {response.text}"

get_stt_data = {
    "encoded_audio": "",
    "lang_code": "en-us",
}
response = requests.post("http://localhost:8082/neon/get_stt", data=get_stt_data, headers=headers, timeout=10)
assert response.status_code == 200
assert response.json().get("transcripts")[0] == "hello"

# /neon/get_response
get_response_data = {
    "lang_code": "en-us",
    "user_profile": {"location": {"city": "Los Angeles", "state": "California"}},
    "utterance": "what time is it",
}

response = requests.post(
    "http://localhost:8082/neon/get_response", data=get_response_data, headers=headers, timeout=10
)
assert response.status_code == 200
assert response.json().get("answer")
